class Solution:
    def reverse_str(self, s: str) -> str:
        for length in range(len(s), -1, -1):
            for index in range(0, len(s) - length + 1):
                ss = s[index:index + length]
                if ss == s[::-1]:
                    return ss

    def longestPalindrome(self, s: str) -> str:
        n = len(s)
        dp = [[False] * n for _ in range(n)]
        ans = ""
        # 枚举子串的长度 l+1
        for l in range(n):
            # 枚举子串的起始位置 i，这样可以通过 j=i+l 得到子串的结束位置
            for i in range(n):
                j = i + l
                if j >= len(s):
                    break
                if l == 0:
                    dp[i][j] = True
                elif l == 1:
                    dp[i][j] = (s[i] == s[j])
                else:
                    dp[i][j] = (dp[i + 1][j - 1] and s[i] == s[j])
                if dp[i][j] and l + 1 > len(ans):
                    ans = s[i:j + 1]
        return ans

    def spread(self, s, left, right):
        while left >= 0 and right < len(s) and s[left] == s[right]:
            left -= 1
            right += 1
        return s[left + 1:right]

    def spread_from_center(self, s):
        if s == s[::-1]:
            return s
        res = ''
        for i in range(len(s)):
            palindrome_odd = self.spread(s, i, i)
            palindrome_even = self.spread(s, i, i + 1)
            res = max(res, palindrome_even, palindrome_odd, key=len)
        return res


if __name__ == '__main__':
    s = Solution()
    sss = [
        "",
        'babad']
    # ss='abcdefedcba'

    for ss in sss:
        print(s.longestPalindrome(ss))
        print(s.reverse_str(ss))
        print(s.spread_from_center(ss))
